package com.class14;

public class ReplaceAll {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String str="hello Dear you, how are you?";



System.out.println(str.replace('e', 'z'));

System.out.println(str.replaceAll("lo", "le"));
System.out.println(str.replaceFirst("you" , "zeynep"));
	}

}
