package com.class8;

public class Break1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		//continue    it will skip current iteration
		
		
		
		
		
		for(int i=1; i<=5; i++) {
			if(i==3) {
			continue;	
			}
			
			System.out.println(i);
			
		}
		System.out.println("i am outside of loop");
		
	}

}
