package com.home.data;

public class WhileHome {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int i=1;
		    while(i<=10){
		      
		      
		        System.out.println(i);
		        
		        
		      if (i==4) {
		        System.out.println(i+=2);
		        System.out.println(i+=4);
		      }
		     i++;
		    }
		      
	}

}
